import React, { useState } from 'react';
import HabitCalendar from './HabitCalendar';

const Habit = ({ habit, deleteHabit, trackHabit, addNote }) => {
  const [showCalendar, setShowCalendar] = useState(false);
  const [note, setNote] = useState('');

  const getStreak = () => {
    let streak = 0;
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    let increment;
    switch (habit.frequency) {
      case 'weekly':
        increment = 7;
        break;
      case 'monthly':
        increment = 30;
        break;
      default:
        increment = 1;
    }

    for (let i = habit.history.length - 1; i >= 0; i--) {
      const date = new Date(habit.history[i]);
      date.setHours(0, 0, 0, 0);

      if (today.getTime() === date.getTime()) {
        streak++;
        today.setDate(today.getDate() - increment);
      } else if (today.getTime() > date.getTime()) {
        break;
      }
    }

    return streak;
  };

  const handleAddNote = () => {
    if (note) {
      addNote(habit.id, note);
      setNote('');
    }
  };

  return (
    <div className="card mb-3">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center">
          <div>
            <h5 className="card-title">{habit.name}</h5>
            <p className="card-text">Streak: {getStreak()}</p>
          </div>
          <div>
            <button
              className="btn btn-success me-2"
              onClick={() => trackHabit(habit.id)}
            >
              <i className="bi bi-check-lg"></i>
            </button>
            <button
              className="btn btn-primary me-2"
              onClick={() => setShowCalendar(!showCalendar)}
            >
              <i className="bi bi-calendar-event"></i>
            </button>
            <button
              className="btn btn-danger"
              onClick={() => deleteHabit(habit.id)}
            >
              <i className="bi bi-trash"></i>
            </button>
          </div>
        </div>
        {showCalendar && <HabitCalendar habit={habit} />}
        <div className="mt-3">
          <h6>Notes</h6>
          <ul className="list-group">
            {habit.notes && habit.notes.map((n) => (
              <li key={n.id} className="list-group-item">
                {n.text}
              </li>
            ))}
          </ul>
          <div className="input-group mt-2">
            <input
              type="text"
              className="form-control"
              placeholder="Add a note..."
              value={note}
              onChange={(e) => setNote(e.target.value)}
            />
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={handleAddNote}
            >
              Add Note
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Habit;
