import React, { useState, useMemo } from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import 'react-calendar-heatmap/dist/styles.css';

const StreakHeatmap = ({ habits, categories }) => {
  const today = new Date();
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedMonth, setSelectedMonth] = useState(today.getMonth());
  const [selectedYear, setSelectedYear] = useState(today.getFullYear());

  const months = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const years = useMemo(() => {
    const currentYear = new Date().getFullYear();
    const yearsArray = [];
    for (let i = currentYear - 5; i <= currentYear + 1; i++) { // Show current year +/- 5 years
      yearsArray.push(i);
    }
    return yearsArray;
  }, []);

  const filteredHabits = useMemo(() => {
    if (selectedCategory === '') {
      return habits;
    }
    return habits.filter(habit => habit.category === selectedCategory);
  }, [habits, selectedCategory]);

  const getValuesForHeatmap = useMemo(() => {
    const dailyCompletions = {};

    filteredHabits.forEach(habit => {
      habit.history.forEach(entry => {
        const date = entry.date; // Assuming date is already YYYY-MM-DD string
        const completions = entry.completions || 0;
        const target = habit.targetCompletions || 1;

        if (!dailyCompletions[date]) {
          dailyCompletions[date] = { completed: 0, target: 0 };
        }
        dailyCompletions[date].completed += completions;
        dailyCompletions[date].target += target;
      });
    });

    const values = [];
    for (const dateString in dailyCompletions) {
      const { completed, target } = dailyCompletions[dateString];
      const ratio = target > 0 ? Math.min(completed / target, 1) : 0; // Cap ratio at 1
      values.push({
        date: dateString,
        count: ratio, // Store ratio as count
      });
    }
    return values;
  }, [filteredHabits]);

  const firstDayOfMonth = new Date(selectedYear, selectedMonth, 1);
  const lastDayOfMonth = new Date(selectedYear, selectedMonth + 1, 0);

  return (
    <div className="card shadow-sm mt-4">
      <div className="card-body">
        <h5 className="card-title text-primary mb-3">Streak Heatmap</h5>
        <div className="row mb-3">
          <div className="col-md-4">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              {categories.map(cat => (
                <option key={cat.id} value={cat.name}>{cat.name}</option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select
              className="form-select"
              value={selectedMonth}
              onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
            >
              {months.map((month, index) => (
                <option key={index} value={index}>{month}</option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select
              className="form-select"
              value={selectedYear}
              onChange={(e) => setSelectedYear(parseInt(e.target.value))}
            >
              {years.map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>
        </div>
        <div className="col-md-6 mx-auto compact-heatmap">
          <CalendarHeatmap
            startDate={firstDayOfMonth}
            endDate={lastDayOfMonth}
            values={getValuesForHeatmap}
            classForValue={(value) => {
              if (!value || value.count === 0) {
                return 'color-empty';
              }
              // Map ratio (0-1) to a color scale (e.g., 1 to 5)
              const intensity = Math.ceil(value.count * 5); // 1-5 scale
              return `color-scale-${intensity}`;
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default StreakHeatmap;
