import React, { useState, useEffect } from 'react';
import HabitList from './components/HabitList';
import AddHabitForm from './components/AddHabitForm';

const App = () => {
  const [habits, setHabits] = useState([]);
  const [categories, setCategories] = useState([
    { id: 1, name: 'Health' },
    { id: 2, name: 'Work' },
    { id: 3, name: 'Personal' },
  ]);
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    const storedHabits = JSON.parse(localStorage.getItem('habits'));
    if (storedHabits) {
      setHabits(storedHabits);
    }
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) {
      setTheme(storedTheme);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('habits', JSON.stringify(habits));
    localStorage.setItem('theme', theme);
    document.body.className = theme === 'dark' ? 'bg-dark text-white' : '';
  }, [habits, theme]);

  const addHabit = (habit) => {
    setHabits([
      ...habits,
      { ...habit, id: Date.now(), history: [], category: 'Uncategorized', notes: [], frequency: habit.frequency || 'daily' },
    ]);
  };

  const deleteHabit = (id) => {
    setHabits(habits.filter((habit) => habit.id !== id));
  };

  const trackHabit = (id) => {
    setHabits(
      habits.map((habit) =>
        habit.id === id
          ? { ...habit, history: [...habit.history, new Date()] }
          : habit
      )
    );
  };

  const addNote = (id, note) => {
    setHabits(
      habits.map((habit) =>
        habit.id === id
          ? { ...habit, notes: [...habit.notes, { id: Date.now(), text: note }] }
          : habit
      )
    );
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={`container mt-5 ${theme}`}>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-center">Habit Tracker</h1>
        <button className="btn btn-secondary" onClick={toggleTheme}>
          <i className={`bi bi-${theme === 'light' ? 'moon' : 'sun'}`}></i>
        </button>
      </div>
      <AddHabitForm addHabit={addHabit} categories={categories} />
      <HabitList
        habits={habits}
        deleteHabit={deleteHabit}
        trackHabit={trackHabit}
        addNote={addNote}
        categories={categories}
      />
    </div>
  );
};

export default App;
