import React, { useState, useMemo } from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import 'react-calendar-heatmap/dist/styles.css';

const StreakHeatmap = ({ habits, categories }) => {
  const today = new Date();
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedMonth, setSelectedMonth] = useState(today.getMonth());
  const [selectedYear, setSelectedYear] = useState(today.getFullYear());

  const months = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const years = useMemo(() => {
    const currentYear = new Date().getFullYear();
    const yearsArray = [];
    for (let i = currentYear - 5; i <= currentYear + 1; i++) {
      yearsArray.push(i);
    }
    return yearsArray;
  }, []);

  const filteredHabits = useMemo(() => {
    if (selectedCategory === '') {
      return habits;
    }
    return habits.filter(habit => habit.category === selectedCategory);
  }, [habits, selectedCategory]);

  const getValuesForHeatmap = useMemo(() => {
    const dailyCompletions = {};

    filteredHabits.forEach(habit => {
      habit.history.forEach(entry => {
        const date = entry.date;
        const completions = entry.completions || 0;
        const target = habit.targetCompletions || 1;

        if (!dailyCompletions[date]) {
          dailyCompletions[date] = { completed: 0, target: 0 };
        }
        dailyCompletions[date].completed += completions;
        dailyCompletions[date].target += target;
      });
    });

    const values = [];
    for (const dateString in dailyCompletions) {
      const { completed, target } = dailyCompletions[dateString];
      const ratio = target > 0 ? Math.min(completed / target, 1) : 0;
      values.push({
        date: dateString,
        count: ratio,
      });
    }
    return values;
  }, [filteredHabits]);

  const firstDayOfMonth = new Date(selectedYear, selectedMonth, 1);
  const lastDayOfMonth = new Date(selectedYear, selectedMonth + 1, 0);

  return (
    <div className="card shadow-sm mt-4" style={{ 
      fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif',
      maxWidth: '896px',
      margin: '0 auto'
    }}>
      <div className="card-body" style={{ padding: '16px' }}>
        <h5 className="card-title" style={{ 
          color: '#24292f',
          fontSize: '14px',
          marginBottom: '16px'
        }}>
          Streak Heatmap
        </h5>

        <div className="row mb-3">
          <div className="col-md-4">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              style={{
                padding: '4px 8px',
                borderRadius: '4px',
                border: '1px solid #d0d7de',
                fontSize: '12px',
                color: '#24292f'
              }}
            >
              <option value="">All Categories</option>
              {categories.map(cat => (
                <option key={cat.id} value={cat.name}>{cat.name}</option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select
              className="form-select"
              value={selectedMonth}
              onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
              style={{
                padding: '4px 8px',
                borderRadius: '4px',
                border: '1px solid #d0d7de',
                fontSize: '12px',
                color: '#24292f'
              }}
            >
              {months.map((month, index) => (
                <option key={index} value={index}>{month}</option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select
              className="form-select"
              value={selectedYear}
              onChange={(e) => setSelectedYear(parseInt(e.target.value))}
              style={{
                padding: '4px 8px',
                borderRadius: '4px',
                border: '1px solid #d0d7de',
                fontSize: '12px',
                color: '#24292f'
              }}
            >
              {years.map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Heatmap Container */}
        <div style={{ 
          backgroundColor: '#f6f8fa',
          borderRadius: '6px',
          padding: '16px',
          marginBottom: '16px'
        }}>
          <div style={{ overflowX: 'auto' }}>
            <CalendarHeatmap
              startDate={firstDayOfMonth}
              endDate={lastDayOfMonth}
              values={getValuesForHeatmap}
              classForValue={(value) => {
                if (!value || value.count === 0) return 'color-empty';
                const intensity = Math.min(Math.ceil(value.count * 4), 4);
                return `color-github-${intensity}`;
              }}
              showWeekdayLabels={true}
              showMonthLabels={false}
              gutterSize={2}
              horizontal={true}
              weekdayLabels={['', 'Mon', '', 'Wed', '', 'Fri', '']}
              style={{ padding: 0 }}
            />
          </div>

          {/* Legend */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'flex-end',
            marginTop: '8px',
            fontSize: '10px',
            color: '#57606a'
          }}>
            <span style={{ marginRight: '8px' }}>Less</span>
            {[0, 1, 2, 3, 4].map((level) => (
              <svg key={level} width="10" height="10" style={{ margin: '0 1px' }}>
                <rect width="10" height="10" rx="2" ry="2" className={`color-github-${level}`} />
              </svg>
            ))}
            <span style={{ marginLeft: '8px' }}>More</span>
          </div>
        </div>

        <style>
          {`
            .react-calendar-heatmap {
              width: 100%;
              font-size: 10px;
            }
            .react-calendar-heatmap-weekday-labels {
              font-size: 9px;
              fill: #57606a;
            }
            .color-empty {
              fill: #ebedf0;
              rx: 2px;
              ry: 2px;
            }
            .color-github-1 { fill: #9be9a8; }
            .color-github-2 { fill: #40c463; }
            .color-github-3 { fill: #30a14e; }
            .color-github-4 { fill: #216e39; }
            .react-calendar-heatmap rect {
              width: 11px;
              height: 11px;
              rx: 2px;
              ry: 2px;
            }
            .react-calendar-heatmap rect:hover {
              stroke: #000;
              stroke-width: 1px;
            }
          `}
        </style>
      </div>
    </div>
  );
};

export default StreakHeatmap;
