import React, { useState, useMemo } from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import 'react-calendar-heatmap/dist/styles.css';

const StreakHeatmap = ({ habits, categories }) => {
  const today = new Date();
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedMonth, setSelectedMonth] = useState(today.getMonth());
  const [selectedYear, setSelectedYear] = useState(today.getFullYear());

  const months = [
    "Jan", "Feb", "Mar", "Apr", "May", "Jun",
    "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"
  ];

  const years = useMemo(() => {
    const currentYear = new Date().getFullYear();
    return Array.from({length: 4}, (_, i) => currentYear - i);
  }, []);

  const filteredHabits = useMemo(() => {
    return selectedCategory ? 
      habits.filter(habit => habit.category === selectedCategory) : 
      habits;
  }, [habits, selectedCategory]);

  const getValuesForHeatmap = useMemo(() => {
    const dailyCompletions = {};
    filteredHabits.forEach(habit => {
      habit.history.forEach(entry => {
        if (!dailyCompletions[entry.date]) {
          dailyCompletions[entry.date] = { completed: 0, target: 0 };
        }
        dailyCompletions[entry.date].completed += entry.completions || 0;
        dailyCompletions[entry.date].target += habit.targetCompletions || 1;
      });
    });
    return Object.entries(dailyCompletions).map(([date, {completed, target}]) => ({
      date,
      count: target > 0 ? Math.min(completed / target, 1) : 0
    }));
  }, [filteredHabits]);

  const firstDayOfMonth = new Date(selectedYear, selectedMonth, 1);
  const lastDayOfMonth = new Date(selectedYear, selectedMonth + 1, 0);

  return (
    <div style={{ 
      fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif',
      maxWidth: '896px',
      margin: '0 auto',
      padding: '8px'
    }}>
      <div style={{ 
        backgroundColor: '#f6f8fa',
        borderRadius: '6px',
        padding: '8px',
        marginBottom: '8px'
      }}>
        {/* Compact Header Row */}
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '8px',
          gap: '8px'
        }}>
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            style={{
              padding: '2px 4px',
              borderRadius: '4px',
              border: '1px solid #d0d7de',
              fontSize: '10px',
              flex: 1
            }}
          >
            <option value="">All Categories</option>
            {categories.map(cat => (
              <option key={cat.id} value={cat.name}>{cat.name}</option>
            ))}
          </select>

          <select
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
            style={{
              padding: '2px 4px',
              borderRadius: '4px',
              border: '1px solid #d0d7de',
              fontSize: '10px',
              flex: 1
            }}
          >
            {months.map((month, index) => (
              <option key={index} value={index}>{month}</option>
            ))}
          </select>

          <select
            value={selectedYear}
            onChange={(e) => setSelectedYear(parseInt(e.target.value))}
            style={{
              padding: '2px 4px',
              borderRadius: '4px',
              border: '1px solid #d0d7de',
              fontSize: '10px',
              flex: 1
            }}
          >
            {years.map(year => (
              <option key={year} value={year}>{year}</option>
            ))}
          </select>
        </div>

        {/* Ultra-Compact Heatmap */}
        <div style={{ overflowX: 'auto', padding: '2px 0' }}>
          <CalendarHeatmap
            startDate={firstDayOfMonth}
            endDate={lastDayOfMonth}
            values={getValuesForHeatmap}
            classForValue={(value) => {
              if (!value || value.count === 0) return 'color-empty';
              return `color-github-${Math.min(Math.ceil(value.count * 4), 4)}`;
            }}
            showWeekdayLabels={true}
            showMonthLabels={false}
            gutterSize={1}
            horizontal={true}
            weekdayLabels={['', 'M', '', 'W', '', 'F', '']}
            style={{ padding: 0 }}
          />
        </div>

        {/* Tiny Legend */}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'flex-end',
          marginTop: '4px',
          fontSize: '8px',
          color: '#57606a'
        }}>
          <span style={{ marginRight: '4px' }}>Less</span>
          {[0, 1, 2, 3, 4].map((level) => (
            <svg key={level} width="8" height="8" style={{ margin: '0 1px' }}>
              <rect width="8" height="8" rx="1" ry="1" className={`color-github-${level}`} />
            </svg>
          ))}
          <span style={{ marginLeft: '4px' }}>More</span>
        </div>
      </div>

      <style>
        {`
          .react-calendar-heatmap {
            width: 100%;
            font-size: 8px;
          }
          .react-calendar-heatmap-weekday-labels {
            font-size: 7px;
            fill: #57606a;
          }
          .color-empty {
            fill: #ebedf0;
            rx: 1px;
            ry: 1px;
          }
          .color-github-1 { fill: #9be9a8; }
          .color-github-2 { fill: #40c463; }
          .color-github-3 { fill: #30a14e; }
          .color-github-4 { fill: #216e39; }
          .react-calendar-heatmap rect {
            width: 8px;
            height: 8px;
            rx: 1px;
            ry: 1px;
          }
          .react-calendar-heatmap rect:hover {
            stroke: #000;
            stroke-width: 0.5px;
          }
        `}
      </style>
    </div>
  );
};

export default StreakHeatmap;
